// Event listener para quando o DOM estiver completamente carregado
/**
 * Função chamada quando o documento é carregado e pronto.
 * Faz uma requisição AJAX para verificar o tipo de utilizador (Admin ou User).
 * Ajusta a visibilidade de vários links e botões na página com base no tipo de utilizador.
 */
document.addEventListener('DOMContentLoaded', async function () {
    $.ajax({
        type: 'GET',
        url: '/termsAndConditions/admin',
        dataType: 'json',
        success: function (response) {
            if (response.success) {
                if (response.message === 'Admin') {
                    $('#homeLink1').show();
                    $('#homeLink').hide();
                    $('#homeLink2').hide();
                    $('#accountLink').hide();
                    $('#adminServicesLink').show();
                    $('#aboutLink').show();
                    $('#servicesLink').show();
                    $('#contactLink').show();
                    $('#aboutLink1').hide();
                    $('#servicesLink1').hide();
                    $('#contactLink1').hide();
                    $('.shopping-cart').show();
                    $('.profile-button').show();
                    $('.logout-button').show();
                    $('#Login').hide();
                } else if (response.message === 'User') {
                    $('#homeLink1').hide();
                    $('#homeLink2').hide();
                    $('#homeLink').show();
                    $('#accountLink').show();
                    $('#adminServicesLink').hide();
                    $('.shopping-cart').show();
                    $('.profile-button').show();
                    $('.logout-button').show();
                    $('#aboutLink').show();
                    $('#servicesLink').show();
                    $('#contactLink').show();
                    $('#aboutLink1').hide();
                    $('#servicesLink1').hide();
                    $('#contactLink1').hide();
                    $('#Login').hide();
                }
            }
        },
        error: function (xhr, status, error) {
            $('#homeLink').hide();
            $('#homeLink1').hide();
            $('#aboutLink').hide();
            $('#servicesLink').hide();
            $('#contactLink').hide();
            $('#accountLink').hide();
            $('.shopping-cart').hide();
            $('.profile-button').hide();
            $('.logout-button').hide();
            $('#aboutLink1').show();
            $('#servicesLink1').show();
            $('#contactLink1').show();
            $('#homeLink2').show();
            $('#adminServicesLink').hide();
            $('#Login').show();
        }
    });
});

// Requisição AJAX para obter o número de itens no carrinho
$.ajax({
    type: 'GET',
    url: '/termsAndConditions/cartItemCount',
    success: function (response) {
        // Verifica se a solicitação foi bem-sucedida e se o número de produtos foi retornado
        if (response.success && response.numberOfProducts !== undefined) {
            // Atualiza o conteúdo da classe cart-item-count-circle com o número de produtos
            $('.cart-item-count-circle').text(response.numberOfProducts);
        } else {
            console.error('Erro ao obter o número de itens no carrinho:', response.message);
        }
    },
    error: function (xhr, status, error) {
        console.error('Erro na solicitação AJAX:', error);
    }
});

// Seleciona o botão de logout utilizando a classe '.logout-button'
var logoutButton = document.querySelector('.logout-button');

if (logoutButton) {
     // Adiciona um evento de clique ao botão de logout
    logoutButton.addEventListener('click', function (event) {
        event.preventDefault(); 

        // Faz uma requisição para o endpoint '/termsAndConditions/logout' usando o método POST
        fetch('/termsAndConditions/logout', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
        })
            .then(function (response) {
                if (response.ok) {
                    window.location.href = '/'; 
                } else {
                    console.error('Erro ao fazer logout:', response.statusText);
                }
            })
            .catch(function (error) {
                console.error('Erro ao fazer logout:', error);
            });
    });
}